    function renderAdminListItem(container, docId, platform, itemData, deleteHandler, editHandler) { if (!container) { console.warn("List container not found for platform:", platform); return; } const itemDiv = document.createElement('div'); itemDiv.className = 'list-item-admin'; itemDiv.setAttribute('data-id', docId); const nickname = itemData.nickname || 'N/A'; const username = itemData.username || 'N/A'; const order = itemData.order ?? 'N/A'; const isVerified = itemData.isVerified || false; let countText = ''; if (platform === 'youtube') { const subscribers = itemData.subscribers || 'N/A'; countText = `Subs: ${subscribers}`; } else if (platform === 'tiktok' || platform === 'instagram') { const followers = itemData.followers || 'N/A'; countText = `Followers: ${followers}`; } let directLinkUrl = '#'; let safeUsername = username || ''; if (platform === 'tiktok' && safeUsername) { directLinkUrl = `https://tiktok.com/@${encodeURIComponent(safeUsername)}`; } else if (platform === 'instagram' && safeUsername) { directLinkUrl = `https://instagram.com/${encodeURIComponent(safeUsername)}`; } else if (platform === 'youtube' && safeUsername) { let youtubeHandle = safeUsername.startsWith('@') ? safeUsername : `@${safeUsername}`; directLinkUrl = `https://www.youtube.com/${encodeURIComponent(youtubeHandle)}`; } const verifiedIndicator = isVerified ? '<span class="verified-indicator" title="Verified">âœ…</span>' : ''; itemDiv.innerHTML = `<div class="item-content"><div class="item-details"><strong>${nickname}</strong> ${verifiedIndicator} <span>(@${username})</span><small>Order: ${order} | ${countText}</small></div></div><div class="item-actions"><a href="${directLinkUrl}" target="_blank" rel="noopener noreferrer" class="direct-link small-button" title="Visit Profile/Channel"><i class="fas fa-external-link-alt"></i> Visit</a><button type="button" class="edit-button small-button">Edit</button><button type="button" class="delete-button small-button">Delete</button></div>`; const editButton = itemDiv.querySelector('.edit-button'); if (editButton) editButton.addEventListener('click', () => editHandler(docId, platform)); const deleteButton = itemDiv.querySelector('.delete-button'); if (deleteButton) deleteButton.addEventListener('click', () => deleteHandler(docId, platform, itemDiv)); container.appendChild(itemDiv); }
    function renderTikTokCard(account) { const profilePic = account.profilePic || 'images/default-profile.jpg'; const username = account.username || 'N/A'; const nickname = account.nickname || 'N/A'; const bio = account.bio || ''; const followers = account.followers || 'N/A'; const isVerified = account.isVerified || false; const profileUrl = username !== 'N/A' ? `https://tiktok.com/@${encodeURIComponent(username)}` : '#'; const verifiedBadge = isVerified ? '<img src="check.png" alt="Verified" class="verified-badge">' : ''; return `<div class="creator-card"><img src="${profilePic}" alt="@${username}" class="creator-pic" onerror="this.onerror=null; this.src='images/default-profile.jpg';"><div class="creator-info"><div class="creator-header"><h3>${nickname} ${verifiedBadge}</h3></div><p class="creator-username">@${username}</p><p class="creator-bio">${bio}</p><p class="follower-count">${followers} Followers</p><a href="${profileUrl}" target="_blank" rel="noopener noreferrer" class="visit-profile"> Visit Profile </a></div></div>`; }
